        msgLEL.ui_control = {
            min: 0,
            max: FullScale
        };
        node.warn("LEL içinde FullScale: " + FullScale);
        node.warn("Gauge için %LEL seçildi.");
        msgVol.payload = 0;
        msgPPM.payload = 0;
        node.send([null, msgLEL, null]);
        break;

    case 'ppm':
        msgPPM.ui_control = {
            min: 0,
            max: FullScale
        };
        node.warn("PPM içinde FullScale: " + FullScale);
        node.warn("Gauge için ppm seçildi.");
        msgVol.payload = 0;
        msgLEL.payload = 0;
        node.send([null, null, msgPPM]);
        break;

    default:
        msgVol.ui_control = {
            min: 0,
            max: FullScale
        };
        node.warn("Varsayılan birim: %VOL kullanılıyor.");
        msgLEL.payload = 0;
        msgPPM.payload = 0;
        node.send([msgVol, null, null]);
        break;
}

return [msgVol, msgLEL, msgPPM];

const inputArray = msg.payload;

//  Numara'dan ASCII'ye Çevirme
function numToAscii(num) {
    var byte1 = num >> 8;
    var byte2 = num & 0xFF;

    var char1 = String.fromCharCode(byte1);
    var char2 = String.fromCharCode(byte2);

    return char1 + char2;
}

var asciiResult = inputArray.slice(0, 4).map(numToAscii).join('');
asciiResult = asciiResult.replace(/\0/g, '');

// Full Scale Hesaplama
var fullScaleBuffer = Buffer.alloc(4);
fullScaleBuffer.writeUInt16LE(inputArray[4], 0);
fullScaleBuffer.writeUInt16LE(inputArray[5], 2);

var fullScale = fullScaleBuffer.readFloatLE(0);
fullScale = Number(fullScale.toFixed(2));

//  Gaz Ölçüm Birimi
var gasUnitBuffer = Buffer.alloc(4);
gasUnitBuffer.writeUInt16BE(inputArray[6], 0);
gasUnitBuffer.writeUInt16BE(inputArray[7], 2);

var gasMeasurementUnit = gasUnitBuffer.toString('ascii').replace(/\0/g, '').trim();

msg.payload = {
    asciiResult: asciiResult,            
    fullScale: fullScale,                 
    gasMeasurementUnit: gasMeasurementUnit 
};
msg.ui_control = {
    min: 0,  
    max: msg.payload.fullScale  
};

return msg;

